import {apiCalls} from "../Axios/Services";

import {
  authenticate,
  logout,
  register,
  categories,
  resetPassword,
  verifyPassword,
  categoryProducts
  ,singleProduct,
  products,
  relatedProducts,
  getWishList,
  addWishList,
  addCart,
  getCart,
  getCartCount,
  updateCart,
  removeCart,
  productSpecification,
  searchProduct,
  hotDeal,
  buyNow,
  banner,
  getProfile,
  updateProfile,
  addAddress,
  getAddress,
  deleteAddresss,
  getOrder,
  getOrderDetail,
  addReview,
  deleteReview,
  myReview,
  getReview,
  checkOut,
  payNow,
  faq

}from "../Redux/ActionType";

const Token = localStorage.getItem("AuthTok");
const Swal = require('sweetalert2')

export function authenticateLogin(data) {
  return (dispatch) =>
    new Promise((resolve, reject) =>
    apiCalls("post", "user/auth/email", data)
        .then((res) => {
          localStorage.setItem("AuthTok", res.data.data.token);
          dispatch({
            type: authenticate.login.success,
            data: res.data,
          });
          resolve(res);
        })
        .catch((err) => {
          dispatch({
            type: authenticate.login.error,
            data: err,
          });
          reject(err);
        })
    );
}

//Registers
export function registerUser(data) {
  return (dispatch) =>
    new Promise((resolve, reject) =>
    apiCalls("post", "/user/customer/register", data)
        .then((res) => {
          dispatch({
            type: register.registerUser.success,
            data: res?.data?.data,
          });
          resolve(true);
        })
        .catch((err) => {
          dispatch({
            type: register.registerUser.error,
            data: err,
          });
          reject(err);
        })
    );
}
// logout
export function logoutUser() {
  return (dispatch) =>
    new Promise((resolve, reject) =>
    apiCalls("post", "/user/auth/logout")
        .then((res) => {
          dispatch({
            type: logout.logoutUser.success,
            data: res?.data?.data,
          });
          resolve(res);
        })
        .catch((err) => {
          dispatch({
            type: logout.logoutUser.error,
            data: err,
          });
          reject(err);
        })
    );
}
export function resetPasswords(data) {
  return (dispatch) =>
    new Promise((resolve, reject) =>
    apiCalls("post", "user/auth/sendEmailCode", data)
